mapper.select:
		select * 
	    from ( 
	        select rownum nrow, n.* 
	        from ( 
				select *
				from dondog_board 
				where delete_status = 'N' 
                order by bnum desc
			) n 

			where rownum <= 6

		) 
		where nrow between 1 and 6

		select * 
	    from ( 
	        select rownum nrow, n.* 
	        from ( 
				select *
				from dondog_board 
				where delete_status = 'N' 
				and email = #{email}
                order by bnum desc
			) n 

			where rownum <= 3

		) 
		where nrow between 1 and 3

		select * 
	    from ( 
	        select rownum nrow, n.* 
	        from ( 
				select *
				from dondog_board 
				where delete_status = 'N' 
                order by bnum desc
			) n 

			where rownum <= 10

		) 
		where nrow between 1 and 10

		select * 
		from ( 
			select rownum nrow, n.* 
			from (
	            select * 
				from dondog_board 
				where delete_status = 'N' 

					and bnum < #{bnum}
                	order by bnum desc
		        ) n 
		        where rownum <= 10

	    ) 
		where nrow between 1 and 10

mapper.insert:
insert into dondog_board_img (
	  inum			
	, bnum 
	, fname 
	, sname 
	, length 
	, ftype 
) values (
	  SEQ_BOARD_IMG.nextval
	, #{bnum}
	, #{fname}
	, #{sname}
	, #{length}
	, #{ftype}	
)

mapper.select:
	select count(*) cnt
    from dondog_heart
    where bnum = #{bnum} 
    and email = #{email}

mapper.delete:
delete from dondog_heart 
where bnum = #{bnum} 
and email = #{email}
