	, #{sname}
	, #{length}
	, #{ftype}	
)

mapper.select:
select * 
from test_coord 
where user_id = #{userId} 
and wnum = #{wnum} 
and walk_date like #{walkDate} || '%' 
order by walk_date

select * from test_coord where user_id = #{userId}
and walk_date like #{walkDate} || '%'
